public class StringImmutabilityDemo {
    public static void main(String[] args) {
        String str1 = "Hello";
        String str2 = str1;

        System.out.println("Before modification:";
        System.out.println("str1: " + str1);
        System.out.println("str2: " + str2);
        System.out.println("str1 == str2: " + (str1 == str2));

        str1 += " World";

        System.out.println("\nAfter modification:";
        System.out.println("str1: " + str1);
        System.out.println("str2: " + str2);
        System.out.println("str1 == str2: " + (str1 == str2));

        // String interning demonstration
        String str3 = "Hello";
        String str4 = new String("Hello");
        String str5 = str4.intern();

        System.out.println("\nString interning:";
        System.out.println("str2 == str3: " + (str2 == str3));
        System.out.println("str3 == str4: " + (str3 == str4));
        System.out.println("str3 == str5: " + (str3 == str5));
    }
}

class MemoryDemo {
    public static void main(String[] args) {
        // Local primitive variable
        int localVar = 10;

        // Object creation (heap allocation)
        Integer boxedInt = Integer.valueOf(20);

        // String literal (string pool in heap)
        String literal = "Hello";

        // String object (heap allocation)
        String object = new String("Hello");

        System.out.println("localVar: " + localVar);
        System.out.println("boxedInt: " + boxedInt);
        System.out.println("literal: " + literal);
        System.out.println("object: " + object);
        System.out.println("literal == object: " + (literal == object));
        System.out.println("literal.equals(object): " + literal.equals(object));

        // Testing garbage collection
        boxedInt = null;
        object = null;
        System.gc(); // Request garbage collection

        // This line will still execute
        System.out.println("After potential garbage collection, literal: " + literal);
    }
}

