public class Test {
    public static void main(String[] args) {
        List<Integer> arrayList = new ArrayList<>();
        arrayList.add(1);
        arrayList.add(2);

        List<Integer> synchronizedList = Collections.synchronizedList(new ArrayList<>());
        synchronizedList.add(1);
        synchronizedList.add(2);

        CopyOnWriteArrayList<Integer> copyOnWriteList = new CopyOnWriteArrayList<>();
        copyOnWriteList.add(1);
        copyOnWriteList.add(2);

        Runnable task = () -> {
            try {
                for (Integer i : copyOnWriteList) {
                    copyOnWriteList.add(3);
                    break;
                }
                System.out.println("Size of CopyOnWriteArrayList: " + copyOnWriteList.size());
            } catch (Exception e) {
                System.out.println("Exception in CopyOnWriteArrayList");
            }

            try {
                synchronized(synchronizedList) {
                    for (Integer i : synchronizedList) {
                        synchronizedList.add(3);
                        break;
                    }
                }
                System.out.println("Size of SynchronizedList: " + synchronizedList.size());
            } catch (Exception e) {
                System.out.println("Exception in SynchronizedList");
            }
        };

        new Thread(task).start();
    }
}

