import java.util.*;

public class ComparatorTest {
    public static void main(String[] args) {
        List<String> fruits = new ArrayList<>();
        fruits.add("Apple");
        fruits.add("Banana");
        fruits.add("Orange");
        fruits.add("Pear");

        Collections.sort(fruits, new LengthThenAlphabeticalComparator());
        System.out.println(fruits);
    }

    static class LengthThenAlphabeticalComparator implements Comparator<String> {
        @Override
        public int compare(String s1, String s2) {
            if (s1.length() != s2.length()) {
                return s2.length() - s1.length(); // Longer strings first
            }
            return s2.compareTo(s1); // Reverse alphabetical for same length
        }
    }
}

