        } catch (DatabaseException e) {
            System.out.println("Caught: " + e.getMessage());
        }
    }

    static void performDatabaseOperation() throws DatabaseException {
        try {
            connectToDatabase();
            // More database operations
        } catch (DatabaseConnectionException e) {
            throw new DatabaseOperationException("Database operation failed: " + e.getMessage());
        }
    }

    static void connectToDatabase() throws DatabaseConnectionException {
        throw new DatabaseConnectionException("Connection timeout");
    }

    static class DatabaseException extends Exception {
