import java.util.concurrent.CompletableFuture;

public class CompletableFutureTest {
    public static void main(String[] args) throws Exception {
        System.out.println("Starting");

        CompletableFuture<Void> future1 = CompletableFuture.runAsync(() -> {
            try {
                Thread.sleep(2000);
                System.out.println("Task 1 completed");
            } catch (InterruptedException e) {
                e.printStackTrace();
            }
        });

        CompletableFuture<Void> future2 = CompletableFuture.runAsync(() -> {
            try {
                Thread.sleep(1000);
                System.out.println("Task 2 completed");
            } catch (InterruptedException e) {
                e.printStackTrace();
            }
        });

        // This will wait for both futures to complete
        CompletableFuture.allOf(future1, future2)
            .thenRun(() -> System.out.println("All tasks completed"));

        // Wait a bit to see the output - in a real app you would use get() or join()
        Thread.sleep(500);

        // The main thread will continue and likely finish before the async tasks
    }
}
