public class SynchronizedCollectionsExample {
    public static void main(String[] args) {
        List<String> normalList = new ArrayList<>();
        normalList.add("one");
        normalList.add("two");

        List<String> synchronizedList = Collections.synchronizedList(new ArrayList<>());
        synchronizedList.add("one");
        synchronizedList.add("two");

        List<String> copyOnWriteList = new CopyOnWriteArrayList<>();
        copyOnWriteList.add("one");
        copyOnWriteList.add("two");

        // Try to modify during iteration
        try {
            Iterator<String> iterator = normalList.iterator();
            while (iterator.hasNext()) {
                String value = iterator.next();
                if (value.equals("one")) {
                    normalList.remove(value);
                }
            }
            System.out.println("Normal list modification succeeded");
        } catch (Exception e) {
            System.out.println("Normal list modification failed: " + e.getClass().getSimpleName());
        }
    }
}

