public class InterruptTest {
    public static void main(String[] args) throws InterruptedException {
        Thread t = new Thread(() -> {
            int counter = 0;
            while (!Thread.currentThread().isInterrupted()) {
                counter++;
                try {
                    // Simulate work
                    if (counter % 3 == 0) {
                        Thread.sleep(10);
                    }
                } catch (InterruptedException e) {
                    System.out.print("Caught: " + counter);
                    break;
                }
            }
            System.out.print(" Final: " + Thread.currentThread().isInterrupted());
        });

        t.start();
        Thread.sleep(20); // Give some time for thread to run
        t.interrupt();
        t.join();
    }
}

public class VolatileTest {
    private static int normalCounter = 0;
    private static volatile int volatileCounter = 0;
    private static boolean normalFlag = false;
    private static volatile boolean volatileFlag = false;

    public static void main(String[] args) throws InterruptedException {
        Thread t1 = new Thread(() -> {
            normalCounter = 100;
            normalFlag = true;

            volatileCounter = 100;
            volatileFlag = true;
        });

        Thread t2 = new Thread(() -> {
            // Which combination is guaranteed to print "Both visible"?
            if (normalFlag && volatileFlag) {
                System.out.println("Both visible: " + normalCounter + ", " + volatileCounter);
            } else if (volatileFlag) {
                System.out.println("Only volatile flag visible");
            } else if (normalFlag) {
                System.out.println("Only normal flag visible");
            } else {
                System.out.println("None visible");
            }
        });

        t1.start();
        // Without proper synchronization, which visibility is guaranteed?
        t2.start();

        t1.join();
        t2.join();
    }
}

