import java.util.HashSet;
import java.util.Objects;

public class EqualsTest {
    static class Product {
        private final String name;
        private final double price;

        public Product(String name, double price) {
            this.name = name;
            this.price = price;
        }

        @Override
        public boolean equals(Object o) {
            if (this == o) return true;
            if (o == null || getClass() != o.getClass()) return false;
            Product product = (Product) o;
            return Double.compare(product.price, price) == 0;
        }

        @Override
        public int hashCode() {
            return Objects.hash(price);
        }

        @Override
        public String toString() {
            return name + ":" + price;
        }
    }

    public static void main(String[] args) {
        HashSet<Product> products = new HashSet<>();
        products.add(new Product("Laptop", 999.99));
        products.add(new Product("Phone", 499.99));
        products.add(new Product("Tablet", 499.99));
        products.add(new Product("Monitor", 299.99));

        System.out.println(products.size());
        System.out.println(products.contains(new Product("NewPhone", 499.99)));
    }
}

import java.util.concurrent.atomic.AtomicInteger;
import java.util.concurrent.atomic.AtomicReference;

public class AtomicTest {
    public static void main(String[] args) throws InterruptedException {
        AtomicInteger counter = new AtomicInteger(0);
        AtomicReference<String> message = new AtomicReference<>("Initial");

        Thread t1 = new Thread(() -> {
            for (int i = 0; i < 1000; i++) {
                counter.incrementAndGet();
                if (i == 500) {
                    message.compareAndSet("Initial", "Changed");
                }
            }
        });

        Thread t2 = new Thread(() -> {
            for (int i = 0; i < 1000; i++) {
                counter.incrementAndGet();
                if (i == 200) {
                    boolean success = message.compareAndSet("Initial", "Failed");
                    System.out.print("CAS Result: " + success + ", ");
                }
            }
        });

        t1.start();
        t2.start();

        t1.join();
        t2.join();

        System.out.println("Counter: " + counter.get() + ", Message: " + message.get());
    }
}

