import java.util.concurrent.locks.Lock;
import java.util.concurrent.locks.ReentrantLock;

public class DeadlockTest {
    private static final Lock lock1 = new ReentrantLock();
    private static final Lock lock2 = new ReentrantLock();
    private static int counter = 0;

    public static void main(String[] args) throws InterruptedException {
        Thread t1 = new Thread(() -> {
            try {
                lock1.lock();
                System.out.print("T1 acquired lock1, ");
                Thread.sleep(50); // Force potential deadlock scenario

                lock2.lock();
                try {
                    System.out.print("T1 acquired lock2, ");
                    counter += 10;
                } finally {
                    lock2.unlock();
                }
            } catch (InterruptedException e) {
                System.out.print("T1 interrupted, ");
            } finally {
                lock1.unlock();
            }
        });

        Thread t2 = new Thread(() -> {
            try {
                lock2.lock();
                System.out.print("T2 acquired lock2, ");

                // Try to acquire lock1 with timeout to avoid deadlock
                if (!lock1.tryLock()) {
                    System.out.print("T2 couldn't acquire lock1, ");
                    counter += 5;
                } else {
                    try {
                        System.out.print("T2 acquired lock1, ");
                        counter += 15;
                    } finally {
                        lock1.unlock();
                    }
                }
            } finally {
                lock2.unlock();
            }
        });

        t1.start();
        Thread.sleep(10); // Ensure T1 gets lock1 first
        t2.start();

        t1.join();
        t2.join();

        System.out.print("Counter: " + counter);
    }
}

