import java.util.HashMap;

public class HashCollisionExample {
    public static void main(String[] args) {
        HashMap<BadHashKey, String> map = new HashMap<>(4, 0.75f);
        // Add elements
        map.put(new BadHashKey(1), "One");
        map.put(new BadHashKey(5), "Five");
        map.put(new BadHashKey(9), "Nine");
        map.put(new BadHashKey(13), "Thirteen");
        // Get element
        System.out.println(map.get(new BadHashKey(5)));
    }
}

class BadHashKey {
    private int value;

    public BadHashKey(int value) {
        this.value = value;
    }

    @Override
    public int hashCode() {
        return value % 4; // Will cause collisions for 1,5,9,13
    }

    @Override
    public boolean equals(Object obj) {
        if (this == obj) return true;
        if (obj == null || getClass() != obj.getClass()) return false;
        BadHashKey other = (BadHashKey) obj;
        return value == other.value;
    }
}

