let counter = 0;

function debounce(func, delay) {
  let timeoutId;
  return function(...args) {
    clearTimeout(timeoutId);
    timeoutId = setTimeout(() => {
      func.apply(this, args);
    }, delay);
  };
}

function increment() {
  counter++;
}

const debouncedIncrement = debounce(increment, 100);

// Simulate rapid calls
