const { Worker, isMainThread, parentPort } = require('worker_threads');

if (isMainThread) {
  const worker = new Worker(__filename);
  worker.on('message', (msg) => {
    console.log('Worker message:', msg);
  });
  worker.postMessage('start');
} else {
  parentPort.on('message', (msg) => {
    const result = performHeavyCalculation();
    parentPort.postMessage(result);
  });
}

function performHeavyCalculation() {
  let result = 0;
  for (let i = 0; i < 1000000; i++) {
    result += i;
  }
  return result;
}

function Point(x, y) {
  this.x = x;
  this.y = y;
}

const points = [];

// Create points with consistent property order
for (let i = 0; i < 5; i++) {
  points.push(new Point(i, i * 2));
}

// Create one point with different property order
const oddPoint = new Point(0, 0);
oddPoint.y = 1; // Delete and reassign to break the hidden class
delete oddPoint.y;
oddPoint.y = 1;

function sumPoints(points) {
  let sum = 0;
  for (let i = 0; i < points.length; i++) {
    sum += points[i].x + points[i].y;
  }
  return sum;
}

console.log(sumPoints(points));
points.push(oddPoint);
