def decorator_with_args(arg1):
    def actual_decorator(func):
        def wrapper(*args, **kwargs):
            result = func(*args, **kwargs)
            return f"{arg1}({args[0]}) = {result}"
        return wrapper
    return actual_decorator

x = 5
print(f"Before: {x}")

@decorator_with_args("factorial")
def factorial(n):
    if n <= 1:
        return 1
    return n * factorial(n-1)

x = factorial(3)
print(f"After: {x}")

def fibonacci():
    a, b = 0, 1
    while True:
        yield a
        a, b = b, a + b

fib_seq = []
for i, num in enumerate(fibonacci()):
    if i >= 10:
        break
    fib_seq.append(num)

print(fib_seq)

from collections import deque

class TreeNode:
    def __init__(self, value):
        self.value = value
        self.left = None
        self.right = None

def bfs(root, target):
    if not root:
        return "Not found"

    queue = deque([root])

    while queue:
        node = queue.popleft()

        if node.value == target:
            return f"Found {target}"

        if node.left:
            queue.append(node.left)
        if node.right:
            queue.append(node.right)

    return "Not found"

# Create a simple tree
root = TreeNode(1)
root.left = TreeNode(2)
root.right = TreeNode(3)
root.left.left = TreeNode(4)
root.left.right = TreeNode(5)

print(bfs(root, 5))

