class Rectangle:
    def __init__(self, width, height):
        self._width = width
        self._height = height
        self._area = width * height

    @property
    def area(self):
        return self._area

    @property
    def x(self):
        return self._width * self._height

    @x.setter
    def x(self, value):
        self._width = value // self._height
        self._area = self._width * self._height

rect = Rectangle(4, 6)
rect.x = 42
print(rect.x)

def hash_search(data, target):
    # Create a dictionary for O(1) lookup
    hash_map = {}

    # Fill the hash map with values and their indices
    for i, value in enumerate(data):
        hash_map[value] = i

    # Look up the target in the hash map
    if target in hash_map:
        return f"Found at index {hash_map[target]}"
    else:
        return "Not found"

data = [5, 3, 8, 1, 9]
print(hash_search(data, 3))

