def style_text(style):
    def decorator(func):
        def wrapper(*args, **kwargs):
            result = func(*args, **kwargs)
            if style == 'uppercase':
                return result.upper()
            elif style == 'lowercase':
                return result.lower()
            else:
                return result
        return wrapper
    return decorator

@style_text('uppercase')
def greet(name, sender):
    return f"Hello, {name}! From: {sender}"

