import numpy as np

# Create a 3x3 matrix where each value is a function of its indices
def create_matrix():
    matrix = np.zeros((3, 3))
    for i in range(3):
        for j in range(3):
            matrix[i, j] = j * (i + 1) / 2
    return matrix

